<script>
    var form = {};

    function registrasi() {
        const username = $("#username").val();
        const password = $("#password").val();

        var model = {};
        model.username = username;
        model.password = password;
        form.model = model;

        $.post("<?= base_url(); ?>/Login/simpanRegistrasi", form, function(res) {
            if (typeof res.validasi == 'undefined') {
                Swal.fire({
                    title: 'Berhasil',
                    text: "Akun Berhasil Dibuat",
                    icon: 'success',
                }).then((result) => {
                    window.location.replace("<?= base_url(); ?>/Login");
                })
            } else {
                Swal.fire('Ups', "Username atau Password kosong", 'error');
            }
        }).fail(function(xhr) {
            console.log(xhr);
            Swal.fire('Error', "Server gagal merespon", 'error');
        }).always(function() {
            // app.form.isSaving = false;
        })
    }

    function check() {
        const username = $("#username").val();
        const password = $("#password").val();

        var model = {};
        model.username = username;
        model.password = password;
        form.model = model;

        $.post("<?= current_url(); ?>/Login/registrasi", "", function(res) {}).fail(function(xhr) {
            console.log(xhr);
            Swal.fire('Error', "Server gagal merespon", 'error');
        }).always(function() {
            // app.form.isSaving = false;
        })
    }
</script>

